import React from "react";

export const TODO_ADDED = "add";
export const TODO_TOGGLED = "toggle";
export const TODO_REMOVED = "remove";

export const toggle = (title) => ({
  type: TODO_TOGGLED,
  payload: title,
});

export const add = (title) => ({
  type: TODO_ADDED,
  payload: title,
});

export const remove = (title) => ({
  type: TODO_REMOVED,
  payload: title,
});

export const asyncRemove = (title) => (dispatch) => {
  setTimeout(() => {
    dispatch(remove(title));
  }, 2000);
};
