import { combineReducers } from "redux";

import { ADD_TODO } from "./actions";

const todos = (state = [], { type, payload }) => {
  if (type === ADD_TODO) {
    return [...state, payload];
  }
  return state;
};

export default combineReducers({
  todos,
});
