import { combineReducers } from "redux";
import { TODO_ADDED, TODO_REMOVED, TODO_TOGGLED } from "./actions";

const todos = (state = [], action) => {
  switch (action.type) {
    case TODO_ADDED:
      return [...state, { title: action.payload }];

    case TODO_TOGGLED: {
      const coppy = [...state];

      if (action.payload === action.payload) {
        return coppy.findIndex(({ title }) => title === action.payload);
      }
    }
    case TODO_REMOVED: {
      const copy = [...state];
      const index = copy.findIndex(({ title }) => title === action.payload);
      copy.splice(index, 1);
      return copy;
    }
  }
  return state;
};

export default combineReducers({
  todos: todos,
});
