import { connect } from "react-redux";
import List from "../components/List";
import { toggle, aRemove, remove } from "../redux/actions";

const mapState = (state, { list }) => {
  let items;

  if (list && ["done", "not-done"].includes(list)) {
    items = state.todos.filter(
      ({ done }) => (list === "done" && done) || (list === "not-done" && !done)
    );
  } else {
    items = state.todos;
  }

  return {
    items,
  };
};

const mapDispatch = (dispatch) => ({
  toggle: (title) => dispatch(toggle(title)),
  remove: (title) => dispatch(remove(title)),
  asyncRemove: (title) => dispatch(asyncRemove(title)),
});

export default connect(mapState, mapDispatch)(List);
